comparisons.ends-with:
{% if 'Templates' ends with 's' %}
"Templates" ends with "s"
{% endif %}

comparisons.equal-to:
{% if varX == varY %}
`varX` is equal to `varY`
{% endif %}

comparisons.greater-than-or-equal:
{% if 2 >= 1 %}
2 is greater than or equal to 1
{% endif %}

{% if 2 >= 1 %}
2 is greater than or equal to 2
{% endif %}

comparisons.less-than-or-equal:
{% if 1 <= 2 %}
1 is less than or equal to 2
{% endif %}

{% if 1 <= 1 %}
1 is less than or equal to 1
{% endif %}

comparisons.matches:
{% if phone matches '/^[\\d\\.]+$/' %}
`phone` is a valid phone number
{% endif %}

comparisons.not-equal-to:
{% if varX != varY %}
`varX` is not equal to `varY`
{% endif %}

comparisons.starts-with:
{% if 'Templates' starts with 'T' %}
"Templates" starts with "T"
{% endif %}

comparisons.strict-equal-to:
{% if 1234 === 1234 %}
1234 not strict equal to 1234
{% endif %}

comparisons.strict-not-equal-to:
{% if 1234 !== '1234' %}
1234 is not strict equal to '1234'
{% endif %}

dictsort.by-value:
{%- for key, value in items | dictsort(false, 'value') -%}
{{ value }}:{{ key }}
{% endfor %}

dictsort.case-insensitive:
{%- for key, value in items | dictsort(false) -%}
{{ key }}
{% endfor %}

dictsort.case-sensitive:
{%- for key, value in items | dictsort(true) -%}
{{ key }}
{% endfor %}

dictsort.default:
{%- for key, value in items | dictsort -%}
{{ key }}
{% endfor %}

raw.multiline:
{% raw %}{{ varX }}{% endraw %}
{{ varY }}
{% raw %}
    {{ varZ }}
{% endraw %}
